;; =>
;;; {"type":"html","content":"<span class='clj-var'>#&#x27;htm-seq-replay/spontaneous-gen</span>"}
;; <=

;; @@
(def spgen-preds
  (spontaneous-gen (-> test-model (cx/break :tm))))

(table/table-view
  (keep-indexed
    (fn [i [decods n-pc n-ac]]
        (when (pos? n-ac)
          [i n-pc n-ac
           (->> decods
                (filter #(> (:votes-per-bit %) 2.0))
                (predictions-table))]))
    spgen-preds)
  :columns '[i pred active decodings])
